import './App.css';
import {BrowserRouter as Router, Routes, Route} from "react-router-dom"
import HomePage from './Home/HomePage';
import SneakersPage from './Sneakers/SnearkersPage';
import CartPage from './Cart/CartPage';
import NavBar from './NavBar/NavBar';
import { useEffect, useState } from 'react';
import axios from 'axios'
import {CartContext} from "./Contexts/CartContext"
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
function App() {
  const [allSneakers,setAllSneakers]=useState([]);
  const [myCart,addtoCart] = useState([{}])
  useEffect(()=>{
    async function getData(){
      const res = await axios.get("/v1/sneakers");
      return res;
    }
    getData().then((res)=> setAllSneakers(res.data));
    getData().catch((err)=>console.log(err));
  },[])
  return (
      <CartContext.Provider value= {{myCart,addtoCart}}>
        <Router>
          <NavBar/>
          <div className='page-container'>
            <Routes>
              <Route path="/" element ={<HomePage/>}/>
              <Route path="/sneakers" element ={<SneakersPage allSneakers={allSneakers}/>}/>
              <Route path="/checkout" element ={<CartPage/>}/>
            </Routes>
          </div>
        </Router>
        <ToastContainer
            position="top-right"
            autoClose={5000}
            hideProgressBar={false}
            newestOnTop={false}
            closeOnClick
            rtl={false}
            pauseOnFocusLoss
            draggable
            pauseOnHover
           />

      </CartContext.Provider>

  );
}

export default App;
