import React, { useState } from 'react'
import { SliderData } from './SliderData'
import RightArrow from '../Icons/square-caret-right-solid.svg'
import LeftArrow from '../Icons/square-caret-left-solid.svg'
import '../Slider/Slider.css'
const ImageSlider = ({slides}) => {
    const[current,setCurrent]=useState(0);
    const length=slides.length;
    if(!Array.isArray(slides) || slides.length <=0){
        return null;
    }
    const NextSlide = () =>{
        setCurrent(current=== length-1 ? 0 : current+1)
    }
  return (
    <section className='slider'>
    <li className='left-arrow'><img src={LeftArrow} alt="" /></li>
    <li className='right-arrow' onClick={NextSlide}><img src={RightArrow} alt="" /></li>
    {SliderData.map((slide, index)=>{
        return(
            <img src ={slide.image} alt="" className='image'/>
        )
    })}
    </section>
    
  )
}

export default ImageSlider
