import { useContext } from "react";
import { CartContext } from "../Contexts/CartContext";
import '../Cart/Cart.css'
import Trash from '../Icons/trash-solid.svg'

const CartPage = () => {
    const {myCart,total,addtoCart,setTotal} = useContext(CartContext)
    const handleCheckout = ()=>{
        setTotal(0);
        addtoCart([{}])
    }
    const handleRemoveItems = (id) => {
        let currentItem = myCart;
        // console.log(currentItem)
        let newPrice = 0
        currentItem = currentItem.filter(item => item.id!== id)
        addtoCart(currentItem) 
        newPrice = total - myCart.price() 
        setTotal(newPrice)      
    }
    return ( 
        <>
            <section className="cart-container">
                <div className="cart-header">
                    <div className="cart-items">
                        {myCart.slice(1).map((item)=>{
                            return(
                                <>
                                <div className="cart-item">
                                    <img src={item.imageUrl} alt="" />
                                    {item.name} : {item.price}$   
                                     <img className="trash" src={Trash} onClick={() => handleRemoveItems(item.id)}/>
                                </div>
                               
                                </>
                            )
                        })}
                        <div className="cart-total">
                            Total: {total}$
                        </div>
                        <button className="cart-checkout" onClick={handleCheckout}> Clear</button>
                    </div>
                </div>
            </section>
        </>
    );
}
 
export default CartPage;
